 class Particle {
   float x, y;
   float xspeed, yspeed;
   float size;
   int alpha;
   int c;

   Particle(float x, float y, float xspeed, float yspeed, float size, int c) {
     this.x = x;
     this.y = y;
     this.xspeed = xspeed;
     this.yspeed = yspeed;
     this.size = size;
     this.c = c;
     this.alpha = 255;
   }

   public void update() {
     x += xspeed;
     y += yspeed;
     yspeed += 0.2f; // Add gravity
     xspeed *= 0.99f; // Slow down horizontal speed
     alpha -= 5; // Decrease alpha
   }

   public void display() {
     fill(red(c), green(c), blue(c), alpha);
     ellipse(x, y, size, size);
   }

   public void use(){
    for (int i = 0; i < particles.size(); i++) {
      Particle p = particles.get(i);
      p.update();
      p.display();
    if (p.y > height || p.alpha < 0) {
      particles.remove(i); // Remove particles that have fallen off the screen or fully faded away
    }
  }
   }
 }
